import * as React from "react";
import { Component } from "react";
import { ITitleFilter } from "./../Interfaceses/ITitleFilter";
import { IDateRange } from "./../Interfaceses/IDateRange";
import Nouislider from "nouislider-react";
import styles from "./../styles/titleFilter.module.css";
import Selector from "./basicComponents/selector";
import { ISelector } from "./../Interfaceses/ISelector";

export interface TitleFilterProps {
  generesSelector: ISelector;
}

export interface TitleFilterState {
  filter: ITitleFilter;
}

class TitleFilter extends React.Component<TitleFilterProps, TitleFilterState> {
  state = {
    filter: {
      yearRange: {
        from: 1975,
        to: 2020,
      },
      generes: [],
      titleType: null,
      titleStatus: null,
    },
  };
  render() {
    const filter = { ...this.state.filter };
    const generes = { ...this.props.generesSelector };
    return (
      <div className={styles.container}>
        <div className={styles.flexContainer}>
          <Nouislider
            range={{ min: filter.yearRange.from, max: filter.yearRange.to }}
            start={[filter.yearRange.from, filter.yearRange.to]}
          />
          <Selector selector={generes} />
        </div>
      </div>
    );
  }
}

export default TitleFilter;
