import * as React from 'react'
import Slider, { Settings } from 'react-slick'
import { ITitleBase } from '../Interfaceses/ITitleBase'
import TitleCard from './titleCard'
import styles from './../styles/slider.module.css'
import { IGenere } from './../Interfaceses/IGenere'
import { connect } from 'react-redux'
import { loadTitleBasesSearch } from '../store/slices/TitleSlice'
import * as Redux from 'redux'

export interface TitleSliderProps {}

interface ReduxTitleSliderProps {
  titleBases: Array<ITitleBase>
}

interface DispatchProps {
  loadTitleBases?: () => void
}

type Props = ReduxTitleSliderProps & DispatchProps & TitleSliderProps

export interface TitleSliderState {}
class TitleSlider extends React.Component<Props, TitleSliderState> {
  componentDidMount() {
    console.log(this.props.loadTitleBases)
    if (this.props.loadTitleBases) this.props.loadTitleBases()
  }

  render() {
    // const testElement: ITitleBase = {
    //   titleDetailsId: 222,
    //   id: 12,
    //   name: 'TestAnime',
    //   posterUrl: 'test.jpg',
    //   treeId: 2,
    //   userRating: 9.5,
    //   generes: new Array<IGenere>(),
    // }
    const settings: Settings = {
      dots: false,
      infinite: false,
      speed: 500,
      slidesToShow: 6,
      slidesToScroll: 3,
      arrows: false,
      className: '',
      adaptiveHeight: true,
    }
    return (
      <div>
        <Slider {...settings}>
          {this.props.titleBases.map((tb) => (
            <TitleCard title={tb} />
          ))}
        </Slider>
      </div>
    )
  }
}
const mapStateToProps = (
  state: any,
  ownProps: TitleSliderProps,
): ReduxTitleSliderProps => ({ titleBases: [...state.topRaited] })
const mapDispatchToProps = (
  dispatch: Redux.Dispatch<Redux.AnyAction>,
  ownProps: TitleSliderProps,
): DispatchProps => ({
  loadTitleBases: () => dispatch(loadTitleBasesSearch()),
})
export default connect(mapStateToProps, mapDispatchToProps)(TitleSlider)
