import { createSlice } from "@reduxjs/toolkit";
import { apiCallBegan } from "../actions/api";
import {ITitleBase} from '../../Interfaceses/ITitleBase'
import { store } from "../store";
import { stat } from "fs";

interface TitleBaseSliceState{
  TopRaited: Array<ITitleBase>

}


const initialState: TitleBaseSliceState = {TopRaited:new Array<ITitleBase>()}
export const titleSlice = createSlice({
  name: "titleBases",
  initialState: {TopRaited:[]},
  reducers: {
    titlesRecevied: (state, action) => {
      let receviedTitles : Array<ITitleBase> = [...action.payload]
      state.TopRaited = receviedTitles;
    },
  },
});

export default titleSlice.reducer;
export const { titlesRecevied } = titleSlice.actions;

export const loadTitleBases = () => {
  return apiCallBegan({
    url: "/titleBases",
    onSuccess: titleSlice.actions.titlesRecevied.type,
  })
};
