import { IDateRange } from "./../Interfaceses/IDateRange";
import moment from "moment";
export default (dateRange: IDateRange): string => {
  const from = `C ${moment(dateRange.from).locale("Ru").format("D MMMM")}`;
  const to = `По ${moment(dateRange.to).locale("Ru").format("D MMMM")}`;
  return from + " " + to;
};
