import moment from "moment";
import { ITitleBase } from "./../Interfaceses/ITitleBase";
import { ITitleDropdownList } from "./../Interfaceses/ITitleDropdownList";
import "moment/locale/ru";
import dateFormatter from "./dateFormatter";
import capitalize from "./capitalize";

export interface GroupEntity {}

export let createScheduleChunk = (
  entities: Array<ITitleBase>
): Array<ITitleDropdownList> => {
  let result: Array<ITitleDropdownList> = [];

  entities.forEach((element: ITitleBase) => {
    const locale = capitalize(
      moment(element.episodeReleaseTime).locale("ru").format("dddd")
    );
    if (result.filter((elementCat) => elementCat.name === locale).length > 0) {
      result
        .filter((elementCat) => elementCat.name === locale)[0]
        .titleElements.push(element);
    } else {
      result.push({ name: locale, titleElements: [element] });
    }
  });
  return result;
};

export let createChunkByLastUpdated = (
  entities: Array<ITitleBase>
): Array<ITitleDropdownList> => {
  let result: Array<ITitleDropdownList> = [];
  entities.forEach((element: ITitleBase) => {
    const date = dateFormatter(element.lastUpdated);
    if (result.filter((elementCat) => elementCat.name === date).length > 0) {
      result
        .filter((elementCat) => elementCat.name === date)[0]
        .titleElements.push(element);
    } else {
      result.push({ name: date, titleElements: [element] });
    }
  });

  return result;
};
