import * as React from "react";
import { ITitleElements } from "../Interfaceses/ITitleElements";
import styles from "../styles/titleList.module.css";
import CSSTransition from "react-transition-group";

export interface TitleListProps {
  //listElements: ITitleElements;
  listName: string;
}

export interface TitleListState {
  isActive: Boolean;
  showList: Boolean;
  highlightedHobby: Boolean;
}
class TitleList extends React.Component<TitleListProps, TitleListState> {
  state = {
    isActive: false,
    showList: true,
    highlightedHobby: false,
  };

  clickHandler = () => {
    let isActive = !this.state.isActive;
    this.setState({ isActive });
  };
  switch = () => {
    this.setState((prevState) => ({
      showList: !prevState.showList,
    }));
  };

  listSwitch = () => {
    this.setState((state) => ({
      highlightedHobby: !state.highlightedHobby,
    }));
  };
  render() {
    //const { listElements, listName } = { ...this.props };

    let menu = this.state.isActive ? (
      <div>
        <ul>
          <li>First Item </li>
          <li>Second Item </li>
          <li>Third Item </li>
        </ul>
      </div>
    ) : (
      ""
    );
    return (
      <div className="container">
        <button className="display" onClick={this.switch}>
          Obinna
        </button>
        <TransitionGroup>
          <CSSTransition
            in={this.state.showList}
            timeout={400}
            classNames="list-transition"
            unmountOnExit
            appear
            onEntered={this.listSwitch}
            onExit={this.listSwitch}
          >
            <div className="list-body">
              <ul className="list">
                <li>Writing JavaScript</li>
                <li className="list-item"> Running</li>
                <li className="list-item"> Technical Writing</li>
                <li className="list-item"> Writing Clean code</li>
              </ul>
            </div>
          </CSSTransition>
        </TransitionGroup>
      </div>
    );
  }
}

export default TitleList;
