import * as React from 'react'

import styles from '../styles/titleList.module.css'
import { CSSTransition } from 'react-transition-group'
import { ITitleList } from './../Interfaceses/ITitleList'
import TitleElement from './TitleElement'

export interface TitleListProps {
  elements: ITitleList
}

export interface TitleListState {
  isActive: Boolean
}
class TitleList extends React.Component<TitleListProps, TitleListState> {
  state = {
    isActive: false,
  }

  clickHandler = () => {
    let isActive = !this.state.isActive
    this.setState({ isActive })
  }
  render() {
    const elements = { ...this.props.elements }
    return (
      <div className={styles.flexMainContainer}>
        <div className={styles.flexContainer}>
          <div className={styles.title}>{elements.name}</div>
          <div className={styles.link} onClick={this.clickHandler}>
            {this.state.isActive ? 'Свернуть' : 'Развернуть'}
          </div>
        </div>

        <CSSTransition
          in={this.state.isActive}
          timeout={600}
          appear
          classNames="menu"
        >
          <div className={styles.container}>
            {elements.titleElements.map((title) => (
              <TitleElement title={title} />
            ))}
          </div>
        </CSSTransition>
      </div>
    )
  }
}

export default TitleList
