import * as React from 'react'
import { Component } from 'react'
import { ISelectData } from './../../Interfaceses/ISelectData'
import { ISelectOnChange } from './../../Interfaceses/ISelectOnChange'

export interface SelectProps {
  data: Array<ISelectData>
  onChange: ISelectOnChange
}

export interface SelectState {
  selectedData: Array<ISelectData>
}

class Select extends React.Component<SelectProps, SelectState> {
  state: SelectState = { selectedData: [] }
  private onSelect = (selectedElement: ISelectData) => {
    const selectedData = { ...this.state.selectedData }

    this.state.selectedData.includes(selectedElement)
      ? selectedData.filter((element) => element != selectedElement)
      : selectedData.push(selectedElement)

    this.setState({ selectedData })
    this.props.onChange(selectedData)
  }
  render() {
    const { data } = { ...this.props }
    return (
      <div>
        <select>
          {data.map((element) => {
            ;<option>
              (
              <input
                type="`checkbox`"
                onChange={() => {
                  this.onSelect(element)
                }}
              />
              ){element.name}
            </option>
          })}
        </select>
      </div>
    )
  }
}

export default Select
