import * as React from "react";
import { Component } from "react";
import { ISelector } from "./../../Interfaceses/ISelector";
import styles from "./../../styles/selector.module.css";
import Select from "react-select";
import { IGenere } from "./../../Interfaceses/IGenere";
export interface SelectorProps {
  selector: ISelector;
}

export interface SelectorState {}

class Selector extends React.Component<SelectorProps, SelectorState> {
  state = {
    selectedOption: null,
  };

  handleChange = (selectedOption: any) => {
    this.setState({ selectedOption });
  };
  render() {
    const selector = { ...this.props.selector };
    return (
      <div className={styles.container}>
        <div className={styles.flexContainer}>
          <div className={styles.title}>{selector.name}</div>
          <Select value={selector.elements} onChange={this.handleChange} />
        </div>
      </div>
    );
  }
}

export default Selector;
