import { createSlice } from '@reduxjs/toolkit'
import { apiCallBegan, IApiData } from '../actions/api'
import { ITitleBase } from '../../Interfaceses/ITitleBase'
import { store } from '../store'
import { stat } from 'fs'
import { ITitleFilter } from './../../Interfaceses/ITitleFilter'
import filterToApi from './../../helperFunctions/FilterToApi'

interface TitleBaseSliceState {
  titleSearch: Array<ITitleBase>
  topRaitedTitles: Array<ITitleBase>
  updatedTitles: Array<ITitleBase>
  newTitles: Array<ITitleBase>
}

const initialState: TitleBaseSliceState = {
  titleSearch: [],
  topRaitedTitles: [],
  updatedTitles: [],
  newTitles: [],
}
export const titleSlice = createSlice({
  name: 'titleBases',
  initialState,
  reducers: {
    titleSearchRecevied: (state: TitleBaseSliceState, action) => {
      state.titleSearch = [...state.titleSearch, ...action.payload]
    },
    topRatiedTitlesRecevied: (state: TitleBaseSliceState, action) => {
      state.topRaitedTitles = [...state.topRaitedTitles, ...action.payload]
    },
    updatedTitlesRecevied: (state: TitleBaseSliceState, action) => {
      state.updatedTitles = [...state.updatedTitles, ...action.payload]
    },
    newTitlesRecevied: (state: TitleBaseSliceState, action) => {
      state.newTitles = [...state.newTitles, ...action.payload]
    },
  },
})

export default titleSlice.reducer
export const {
  titleSearchRecevied,
  topRatiedTitlesRecevied,
  updatedTitlesRecevied,
  newTitlesRecevied,
} = titleSlice.actions

export const loadTitleBases = (filter?: ITitleFilter) => {
  if (typeof filter === 'undefined') filter = { ItemsToTake: 5 }
  const apiData: IApiData = {
    url: '/titleBases',
    onSuccess: titleSlice.actions.titleSearchRecevied.type,
    params: filterToApi(filter),
  }

  return apiCallBegan(apiData)
}
