import { IDateRange } from "./../Interfaceses/IDateRange";
import moment from "moment";
export default (dateRange: IDateRange): string => {
  const from = `C ${moment(dateRange.from).lang("ru").format("D MMMM")}`;
  const to = `По ${moment(dateRange.to).lang("ru").format("D MMMM")}`;
  return from + " " + to;
};
