import { createSlice } from '@reduxjs/toolkit'
import { IFilterData } from './../../Interfaceses/IFilterData'
import titleReducer from './titleSlice'
import filterReducer from './filterSlice'

const initialState: IFilterData = {
  generes: [],
  statuses: [],
  types: [],
  yearRange: {
    from: 0,
    to: 0,
  },
}
//сделать универсальный редьюсер
export const filterSlice = createSlice({
  name: 'titleFilter',
  initialState,
  reducers: {
    titleFilterUpdated: (state: any, action) => {
      state = action.payload
    },
    titleFilterRecevied: (state: any, action) => {
      state = action.payload
    },
  },
})

export default filterSlice.reducer
export const { titleFilterUpdated, titleFilterRecevied } = filterSlice.actions
