import * as React from 'react'
import { Component } from 'react'
import { ISelectOption } from './../Interfaceses/ISelectOption'
import SelectReact from 'react-select'
import styles from './../styles/select.module.css'
export interface SelectProps {
  options: Array<ISelectOption>
  isMulti: boolean
  title?: string
  onChange(selectedElement: any): void
}

export interface SelectState {}

class Select extends React.Component<SelectProps, SelectState> {
  private titleRender = (title?: string) => {
    return title === undefined ? null : (
      <span className={styles.title}>{title}</span>
    )
  }
  render() {
    const props = { ...this.props }
    const style = {
      control: (base: any) => ({
        ...base,
        border: '1px solid #e6e6e6',
        borderColor: 'rgba(0,0,0,.24)',
        boxShadow: 'none',
      }),
      menu: (provided: any, state: any) => ({
        ...provided,
        color: 'red',
      }),
    }
    const colourStyles = {
      control: (styles: any) => ({ ...styles, backgroundColor: 'white' }),
      option: (styles: any, { data, isDisabled, isFocused, isSelected }) => {
        return {
          ...styles,
          backgroundColor: isDisabled
            ? null
            : isSelected
            ? data.color
            : isFocused
            ? color.alpha(0.5).css()
            : null,
          color: isDisabled
            ? '#ccc'
            : isSelected
            ? chroma.contrast(color, 'white') > 2
              ? 'white'
              : 'black'
            : data.color,
          cursor: isDisabled ? 'not-allowed' : 'default',

          ':active': {
            ...styles[':active'],
            backgroundColor:
              !isDisabled && (isSelected ? data.color : color.alpha(0.3).css()),
          },
        }
      },
      input: (styles) => ({ ...styles, ...dot() }),
      placeholder: (styles) => ({ ...styles, ...dot() }),
      singleValue: (styles, { data }) => ({ ...styles, ...dot(data.color) }),
    }
    return (
      <div className={styles.container}>
        <div className={styles.flexContainer}>
          {this.titleRender(props.title)}
          <SelectReact
            options={props.options}
            getOptionLabel={(option: ISelectOption) => option.name}
            getOptionValue={(option: ISelectOption) => option.name}
            isMulti={props.isMulti}
            onChange={props.onChange}
            className={styles.select}
            styles={style}
          />
        </div>
      </div>
    )
  }
}

export default Select
