import * as React from 'react'
import { ITitleElements } from '../Interfaceses/ITitleElements'
import styles from '../styles/titleList.module.css'
import { CSSTransition } from 'react-transition-group'
import { ITitleChunk } from './../Interfaceses/ITitleChunk'

export interface TitleListProps {
  elements: ITitleElements
}

export interface TitleListState {
  isActive: Boolean
}
class TitleList extends React.Component<TitleListProps, TitleListState> {
  state = {
    isActive: false,
  }

  clickHandler = () => {
    let isActive = !this.state.isActive
    this.setState({ isActive })
  }
  render() {
    const elements = { ...this.props.elements }
    return (
      <div className={styles.flexMainContainer}>
        <div>
          <div className={styles.flexContainer}>
            <div>{elements.elements.name}</div>
            <div onClick={this.clickHandler}>
              {this.state.isActive ? 'Свернуть' : 'Развернуть'}
            </div>
          </div>
        </div>
        <CSSTransition
          in={this.state.isActive}
          timeout={600}
          appear
          classNames="menu"
        >
          <div>
            <ul>
              <li>First Item </li>
              <li>Second Item </li>
              <li>Third Item </li>
            </ul>
          </div>
        </CSSTransition>
        {}
      </div>
    )
  }
}

export default TitleList[{ name: asdsad, data: array }]
