import * as React from 'react'
import { Component } from 'react'
import { ITitleFilter } from './../Interfaceses/ITitleFilter'
import { IDateRange } from './../Interfaceses/IDateRange'
import Nouislider from 'nouislider-react'
import styles from './../styles/titleFilter.module.css'
import { ISelectData } from './../Interfaceses/ISelectData'
import { IGenere } from './../Interfaceses/IGenere'
import Select from 'react-select'

export interface TitleFilterProps {}

export interface TitleFilterState {
  filter: ITitleFilter
}

class TitleFilter extends React.Component<TitleFilterProps, TitleFilterState> {
  state: TitleFilterState = {
    filter: {
      yearRange: {
        from: 1975,
        to: 2020,
      },
      generes: [],
      titleType: null,
      titleStatus: null,
    },
  }
  handleChange = (selectedOption) => {
    const filter = {
      ...this.state.filter,
    }

    this.setState({ selectedOption })
  }
  render() {
    const filter = { ...this.state.filter }
    const genere = { id: 12, name: 'gavno', label: 'gavno' }
    const genere2 = { id: 13, name: 'gavno', label: 'test' }
    const genere3 = { id: 14, name: 'gavno', label: 'testGavno' }

    const generes = [genere, genere2, genere3]
    return (
      <div className={styles.container}>
        <div className={styles.flexContainer}>
          <Nouislider
            range={{ min: filter.yearRange.from, max: filter.yearRange.to }}
            start={[filter.yearRange.from, filter.yearRange.to]}
          />
          <Select options={generes} />
        </div>
      </div>
    )
  }
}

export default TitleFilter
