import * as React from "react";
import { Component } from "react";
import styles from "./../styles/episodeSelect.module.css";
import Image from "./basicComponents/image";
import { IEpisode } from "./../Interfaceses/IEpisode";
import extractor from "./../helperFunctions/sizeElementExtractor";
export interface EpisodeSelectProps {
  episodes: Array<IEpisode>;
  onSelect(episode: IEpisode): void;
  numberToSkip: number;
}

export interface EpisodeSelectState {
  leftBoundary: number;
  rightBoundary: number;
  firstShowingElement: number;
  lastShowingElement: number;
  elementLengthInPersent: number;
  translateXState: string;
  leftArrowDisabled: boolean;
  rightArrowDisabled: boolean;
  selectedEpisode: IEpisode | null;
}

class EpisodeSelect extends React.Component<
  EpisodeSelectProps,
  EpisodeSelectState
> {
  private episodesContainer = React.createRef<HTMLDivElement>();
  private episode = React.createRef<HTMLDivElement>();
  state: EpisodeSelectState = {
    leftBoundary: 0,
    rightBoundary: 0,
    firstShowingElement: 1,
    lastShowingElement: this.props.numberToSkip,
    elementLengthInPersent: -1,
    translateXState: "0%",
    leftArrowDisabled: false,
    rightArrowDisabled: false,
    selectedEpisode: null,
  };
  componentDidMount() {
    const { current: container }: any = this.episodesContainer;
    const { current: element }: any = this.episode;

    const nodeStyle = window.getComputedStyle(element);
    const elementMarginRight = nodeStyle.getPropertyValue("margin-right");
    const margin = extractor(elementMarginRight);
    const persent =
      (100 * (margin + element.offsetWidth)) / container.offsetWidth;
    console.log(persent);
    const state = { ...this.state };
    state.elementLengthInPersent = persent;

    state.leftBoundary = this.props.episodes[0].episodeNumber;
    state.rightBoundary = this.props.episodes[
      this.props.episodes.length - 1
    ].episodeNumber;
    state.selectedEpisode = this.props.episodes[0];
    this.setState(state);
  }
  select;
  rightClickHandler = () => {
    const state = { ...this.state };
    const prevValue = Number(
      state.translateXState.slice(-1, state.translateXState.length - 1)
    );
    const moveSize = -(this.props.numberToSkip * state.elementLengthInPersent);
    state.translateXState = `${moveSize + prevValue}%`;

    state.firstShowingElement = state.lastShowingElement;
    state.lastShowingElement += this.props.numberToSkip;

    if (state.lastShowingElement >= state.rightBoundary) {
      state.rightArrowDisabled = true;
    } else {
      state.rightArrowDisabled = false;
    }
    this.setState(state);
  };
  leftClickHandler = () => {
    const state = { ...this.state };
    const prevValue = Number(
      state.translateXState.slice(-1, state.translateXState.length - 1)
    );
    const moveSize = this.props.numberToSkip * state.elementLengthInPersent;
    state.translateXState = `${moveSize + prevValue}%`;

    state.lastShowingElement = state.firstShowingElement;
    state.lastShowingElement -= this.props.numberToSkip;
    if (state.firstShowingElement <= state.leftBoundary) {
      state.rightArrowDisabled = true;
    } else {
      state.rightArrowDisabled = false;
    }
    this.setState(state);
  };
  render() {
    const episodes = this.props.episodes;
    return (
      <div className={styles.container}>
        <div className={styles.containerFlex}>
          <div className={styles.episodeSelect}>
            <div className={styles.episodeSelectText}>Серия №</div>
            <input
              className={styles.episodeSelectInput}
              type="number"
              min="0"
              max="23"
            />
          </div>
          <div className={styles.episodeText}>Серия</div>
          <div className={styles.btnContainer}>
            <div
              style={{ transform: `translateX(${this.state.translateXState})` }}
              ref={this.episodesContainer}
              className={styles.btns}
            >
              {episodes.map((episode) => {
                <div
                  onClick={() => {
                    this.props.onSelect(episode);
                  }}
                  ref={this.episode}
                  className={
                    this.state.selectedEpisode ? styles.btnActive : styles.btn
                  }
                >
                  {episode.episodeNumber}
                </div>;
              })}
              <div ref={this.episode} className={styles.btnActive}>
                1
              </div>
              <div className={styles.btn}>2</div>

              <div className={styles.btnActive}>4</div>
              <div className={styles.btnActive}>5</div>
              <div className={styles.btnActive}>6</div>
              <div className={styles.btnActive}>7</div>
              <div className={styles.btnActive}>8</div>
              <div className={styles.btnActive}>9</div>
              <div className={styles.btnActive}>10</div>
              <div className={styles.btnActive}>11</div>
            </div>
          </div>

          <div className={styles.changeBtnFlexContainer}>
            <div
              aria-disabled={this.state.leftArrowDisabled}
              onClick={() => {
                this.leftClickHandler();
              }}
              className={styles.changeBtnLeft}
            >
              <Image
                width={"100%"}
                height={"100%"}
                mode="fill"
                src={require("./../" + "left-arrow.svg")}
              />
            </div>
            <div
              aria-disabled={this.state.rightArrowDisabled}
              onClick={() => {
                this.rightClickHandler();
              }}
              className={styles.changeBtnRight}
            >
              <Image
                width={"100%"}
                height={"100%"}
                mode="fill"
                src={require("./../" + "right-arrow.svg")}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default EpisodeSelect;
