import { createSlice } from "@reduxjs/toolkit";
import { apiCallBegan } from "./../actions/api";
import {ITitleBase} from './../../Interfaceses/ITitleBase'
export const titleSlice = createSlice({
  name: "titles",
  titles: {
    list: new Array<ITitleBase>(),
    loading: false,
  },
  reducers: {
    titlesRecevied: (titles, action) => {
      console.log(action,titles.list);
      titles.list = action.payload;
    },
  },
});

export default titleSlice.reducer;
export const { titlesRecevied } = titleSlice.actions;

export const loadTitleBases = () => {
  return apiCallBegan({
    url: "titleBases",
    onSuccess: titleSlice.actions.titlesRecevied.type,
  });
};
