import * as React from 'react'
import { Component } from 'react'
import styles from './../styles/episodeSelect.module.css'
import Image from './basicComponents/image'
import { IEpisode } from './../Interfaceses/IEpisode'

export interface EpisodeSelectProps {
  // episodes: Array<IEpisode>
  // onSelect(episode: IEpisode): void
  // numberToSkip: number
}

export interface EpisodeSelectState {
  // firstShowingElement: number
  // lastShowingElement: number
  // elementLengthInPersent: number
}

class EpisodeSelect extends React.Component<
  EpisodeSelectProps,
  EpisodeSelectState
> {
  episodesContainer = React.createRef<HTMLDivElement>()
  episode = React.createRef<HTMLDivElement>()
  componentDidMount() {
    const { container }: any = this.episodesContainer
    const { element }: any = this.episode

    console.log(`${container.offsetWidth}, ${container.offsetHeight}`)
    console.log(`${element.offsetWidth}, ${element.offsetHeight}`)
  }
  state: EpisodeSelectState = {
    // firstShowingElement: 1,
    // lastShowingElement: this.props.numberToSkip,
  }
  render() {
    return (
      <div className={styles.container}>
        <div className={styles.containerFlex}>
          <div className={styles.episodeSelect}>
            <div className={styles.episodeSelectText}>Серия №</div>
            <input
              className={styles.episodeSelectInput}
              type="number"
              min="0"
              max="23"
            />
          </div>
          <div className={styles.episodeText}>Серия</div>
          <div className={styles.btnContainer}>
            <div ref={this.episodesContainer} className={styles.btns}>
              <div ref={this.episode} className={styles.btnActive}>
                1
              </div>
              <div className={styles.btn}>2</div>
              <div className={styles.btnActive}>3</div>
              <div className={styles.btnActive}>4</div>
              <div className={styles.btnActive}>5</div>
              <div className={styles.btnActive}>6</div>
              <div className={styles.btnActive}>7</div>
              <div className={styles.btnActive}>8</div>
              <div className={styles.btnActive}>9</div>
              <div className={styles.btnActive}>10</div>
              <div className={styles.btnActive}>11</div>
            </div>
          </div>

          <div className={styles.changeBtnFlexContainer}>
            <div className={styles.changeBtnLeft}>
              <Image
                width={'100%'}
                height={'100%'}
                mode="fill"
                src={require('./../' + 'left-arrow.svg')}
              />
            </div>
            <div className={styles.changeBtnRight}>
              <Image
                width={'100%'}
                height={'100%'}
                mode="fill"
                src={require('./../' + 'right-arrow.svg')}
              />
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default EpisodeSelect
