import * as React from "react";
import { ITitleElements } from "../Interfaceses/ITitleElements";
import styles from "../styles/titleList.module.css";
import { CSSTransition, TransitionGroup } from "react-transition-group";

export interface TitleListProps {
  //listElements: ITitleElements;
  listName: string;
}

export interface TitleListState {
  isActive: Boolean;
  showList: Boolean;
  highlightedHobby: Boolean;
}
class TitleList extends React.Component<TitleListProps, TitleListState> {
  state = {
    isActive: false,
    showList: true,
    highlightedHobby: false,
  };

  clickHandler = () => {
    let isActive = !this.state.isActive;
    this.setState({ isActive });
  };
  switch = () => {
    this.setState((prevState) => ({
      showList: !prevState.showList,
    }));
  };

  listSwitch = () => {
    this.setState((state) => ({
      highlightedHobby: !state.highlightedHobby,
    }));
  };
  render() {
    //const { listElements, listName } = { ...this.props };

    let menu = this.state.isActive ? (
      <div>
        <ul>
          <li>First Item </li>
          <li>Second Item </li>
          <li>Third Item </li>
        </ul>
      </div>
    ) : (
      ""
    );
    return (
      <div className="container">
        <button className="display" onClick={this.switch}>
          Obinna
        </button>
      </div>
    );
  }
}

export default TitleList;
