const mapStateToProps = (state, ownProps) => {
  const titleBases = state.titleBases.slice();
  return {
    topRaitedTitleBases: titleBases.sort((a, b) => a.userRating > b.userRating ? 1 : b.userRating > a.userRating ? -1 : 0).slice(0, 10),
    lastUpdatedTitleBases: titleBases.sort((a, b) => a.lastUpdated > b.lastUpdated ? 1 : b.lastUpdated > a.lastUpdated ? -1 : 0).slice(0, 10),
    newAddedTitleBases: titleBases.sort((a, b) => a.addedOnSite > b.addedOnSite ? 1 : b.addedOnSite > a.addedOnSite ? -1 : 0).slice(0, 10),
    goingTitleBases: titleBases.filter(tb => tb.titleStatus.name.toLowerCase() !== 'finished')
  };
};

const mapDispatchToProps = (dispatch, ownProps) => ({
  loadTitleBases: () => dispatch(loadTitleBases())
});

export default connect(mapStateToProps, mapDispatchToProps)(MainPage);
